mapper.update:
update op_evse_charging_threshold
set deleted = 1
where evse_id = #{id}

update op_evse_charging_threshold
set updated_at = #{updatedAt}, deleted = #{deleted}, status = #{status}
, car_battery_current = #{carBatteryCurrent}, car_battery_voltage = #{carBatteryVoltage}, car_battery_temperature = #{carBatteryTemperature}
, evse_current = #{evseCurrent}, evse_voltage = #{evseVoltage}, evse_temperature = #{evseTemperature}, connector_current = #{connectorCurrent}
, connector_voltage = #{connectorVoltage}, connector_temperature = #{connectorTemperature}, auto_down_current = #{autoDownCurrent}
, auto_down_voltage = #{autoDownVoltage}, auto_down_temperature = #{autoDownTemperature}
where evse_id = #{id}
